import './App.css';
import { useState } from 'react';


const Users=[];


export default function App() {
  return (
    <div className='dashboard'>
      <UserForm />
    </div>
  );
}

function UserForm(){
  const [userForm,setuserForm] = useState({
    fname : "",
    lname : "",
    email : ""
  });

 const handleChange = (event) => {
    const id = event.target.id;
    const value = event.target.value;
    switch (id) {
      case "fname" : setuserForm((prev)=>{return {...prev,fname : value}})
      case "lname" : setuserForm((prev)=>{return {...prev,lname : value}})
      case "email" : setuserForm((prev)=>{return {...prev,email : value}})
    }
  };

  const onFormSubmit = () =>
{
  return <AddUser />;
}
  return(
   <>
   <form>
   <label htmlFor="fname">FirstName : </label>
   <input id="fname" onChange={handleChange}></input><br></br>
   <label htmlFor="lname">LastName : </label>
   <input id="lname" onChange={handleChange}></input><br></br>
   <label htmlFor="email">Email : </label>
   <input id="email" onChange={handleChange}></input><br></br>
   <button onClick={onFormSubmit}>
    Submit
   </button>
   </form>
   </>
  );
}

