import './App.css';
import { useState } from 'react';


const Users=[
  {fname : 'vasumithra', lname : 'gorrepati', email : 'xyz@gmail.com'},
  {fname : 'Mahesh', lname : 'gorrepati', email : 'xyz@gmail.com'},  
];


export default function App() {
  const [adduser,setadduser] = useState(Users); 
  return (
    <div className='dashboard'>
      <UserForm adduser={adduser} setadduser={setadduser}/>
      <DisplayUsers adduser={adduser}/>
    </div>
  );
}

function UserForm({adduser,setadduser}){
  const [userForm,setuserForm] = useState({
    fname : "",
    lname : "",
    email : ""
  });
  const handleChange = (event) => {
    const id = event.target.id;
    const value = event.target.value;
    switch (id) {
      case "fname" : setuserForm((prev)=>{return {...prev,fname : value}})
      case "lname" : setuserForm((prev)=>{return {...prev,lname : value}})
      case "email" : setuserForm((prev)=>{return {...prev,email : value}})
    }
  };
  const onFormSubmit =(e) => {
    setadduser([...adduser,{...userForm}]);
   e.preventDefault();
   e.target.reset();
  }
  return(
   <>
   <form onSubmit={onFormSubmit}>
   <label htmlFor="fname">FirstName : </label>
   <input id="fname" onChange={handleChange}></input><br></br>
   <label htmlFor="lname">LastName : </label>
   <input id="lname" onChange={handleChange}></input><br></br>
   <label htmlFor="email">Email : </label>
   <input id="email" onChange={handleChange}></input><br></br>
   <button type='submit'>
    Submit
   </button>
   </form>
   </>
  );
}

function DisplayUsers(props){
  console.log(props.adduser);
  const listItems = props.adduser.map((element) => {
    return (
        <ul key={element.fname}>
            <li>{element.fname}</li>
            <li>{element.lname}</li>
            <li>{element.email}</li>
        </ul>
    );
});
if(Users.length>0){
return (
<div>{listItems}</div>);
}
else{
  return <div>No Users to display</div>
}
}

